mapper.select:
select

from tb_health_care_chinese_medicine
where itemID = #{itemid,jdbcType=INTEGER}
  and itemCode = #{itemcode,jdbcType=CHAR}
order by itemUpdateAt desc

mapper.delete:
delete from tb_health_care_chinese_medicine
where itemID = #{key.itemid,jdbcType=INTEGER}
  and itemCode = #{key.itemcode,jdbcType=CHAR}

mapper.update:
update tb_health_care_chinese_medicine


    CHINESE_MEDICINE_STATUS = #{status,jdbcType=VARCHAR}


where itemID = #{key.itemid,jdbcType=INTEGER}
and itemCode = #{key.itemcode,jdbcType=CHAR}
