mapper.select:
select

,

from tb_culpro_hosp
where itemID = #{itemid,jdbcType=INTEGER}
and itemCode = #{itemcode,jdbcType=CHAR}

mapper.delete:
delete from tb_culpro_hosp
where itemID = #{itemid,jdbcType=INTEGER}
and itemCode = #{itemcode,jdbcType=CHAR}

mapper.update:
UPDATE tb_culpro_hosp
SET HOSPITAL_STATUS = #{status}, UPDATER = #{updater}
WHERE itemID = #{itemid,jdbcType=INTEGER}
AND itemCode = #{itemcode,jdbcType=CHAR}
